<script type="text/javascript">
  $(".birthday-picker li").click(function(event) {
    UL = $(this).parent('ul').eq(0);
    $(UL).children('.active').removeClass('active');
    $(this).addClass('active');
    forW = $(UL).attr('for');
    $(".birthday-picker .header").find('.'+forW).text($(this).attr("va"));

  });
(function ( $ ) { 
    $.fn.CzBDP_Set = function( yyyymmdd ) {
      $(this).find("li.active").removeClass('active');
      if(typeof yyyymmdd !== 'undefined'){
        var DateIn = yyyymmdd.split('-');
        $(this).find("span.YYYY").text((parseInt(DateIn[0])-1911));
        $(this).find("span.MM").text(DateIn[1]);
        $(this).find("span.DD").text(DateIn[2]);
      }
        
    };
}( jQuery ));
/******************************/

$(".center-total-count").on('click', '.age-button', function(event) {
  event.preventDefault();
  parent = $(this).parents(".people-birthday").eq(0);
  CzDatePicker_target = $(parent).find('input').eq(0);
  $(".birthday-picker").CzBDP_Set($(CzDatePicker_target).attr('yyyymmdd'));
  

  $(".birthday-picker").fadeIn();
  $(".birthday-picker-BG").fadeIn();
  /* Act on the event */
});
$(".birthday-picker-BG, div.birthday-picker > div.footer button.cancel").click(function(event) {
  /* Act on the event */
  $(".birthday-picker").fadeOut();
  $(".birthday-picker-BG").fadeOut();
});
$("div.birthday-picker > div.footer button.accept").click(function(event) {
  /* Act on the event */
  var header = $("div.birthday-picker > div.header").eq(0);
  var DS = $(header).find("span.YYYY").text() + $(header).find("span.MM").text() + $(header).find("span.DD").text();
  $(CzDatePicker_target).val(DS).trigger('change');
  $(".birthday-picker").fadeOut();
  $(".birthday-picker-BG").fadeOut();
});



</script>